INSERT INTO Card (id, name, image) VALUES (1, 'Petr Bezruč', 'resources/img/authors/01bezruc.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (2, 'Narodil se 15. září 1867 v Opavě. Zemřel 17. února 1958 v Olomouci.', 1);
INSERT INTO Bacon (id, value, card_id) VALUES (3, 'Český básník. Vlastním jménem Vladimír Vašek.', 1);
INSERT INTO Bacon (id, value, card_id) VALUES (4, 'Jediné dílo: Slezské písně. Naprosto ojedinělé verše útočně promlouvající za sociální a národní zájmy českých obyvatel Slezska nemají svým vznikem, stylem ani dalším osudem v české literatuře obdobu. Literární historici řadí Bezruče do generace tzv. anarchistických buřičů. Jeho dílo je ovlivněno symbolismem a Českou modernou. Spekuluje se však o tom, že autorem většiny básní ze sbírky Slezské písně ve skutečnosti není Vašek, ale jeho přítel Ondřej Boleslav Peter, který spáchal sebevraždu.', 1);
INSERT INTO Bacon (id, value, card_id) VALUES (5, 'Poezie Petra Bezruče je v kontextu české literatury naprosto ojedinělá. Jeho originalita netkví pouze v (jediném) prudkém básnickém výboji, který se zaobírá sociálními a národnostními problémy Slezska konce 19. století řadí ho tak mezi autory sociální, buřičské. Je to také především zvláštní vztah Vladimíra Vaška k vlastnímu „jedinému“ dílu, ve kterém se dal tak prudce na stranu chudých a utlačovaných. A to pouze jedenkrát za svůj život. Literární teoretici přicházeli s nejrůznějšími teoriemi, které měly vysvětlit mnoho nejasností ohledně života a díla Petra Bezruče, ty však nejsou dodnes uspokojivě vysvětleny.', 1);
INSERT INTO Bacon (id, value, card_id) VALUES (6, 'Stavba jeho básní je dramaticky vzdorná a útočná, používáním výhružných apostrof (zvolání), hyperbol, kontrastů, sžíravého sarkasmu, záměrného porušování rytmu, využívání nářečních prvků, metafor a zvukomalby nás nutí zamyslet se nad tématy jeho básní.', 1);
INSERT INTO Bacon (id, value, card_id) VALUES (7, 'Některé básně z této sbírky zhudebnil a nazpíval písničkář Jarek Nohavica.', 1);
INSERT INTO Card (id, name, image) VALUES (19, 'Jaroslav Foglar', 'resources/img/authors/01foglar.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (20, 'Narozen 6. července 1907 v Praze. Zemřel 23. ledna 1999 taktéž v Praze.', 19);
INSERT INTO Bacon (id, value, card_id) VALUES (21, 'Významný český spisovatel literatury pro mládež, redaktor několika dětských časopisů a zážitkový pedagog.', 19);
INSERT INTO Bacon (id, value, card_id) VALUES (22, 'Pod svou skautskou přezdívkou Jestřáb vedl po značnou část svého života chlapecký (zpočátku klasický skautský) oddíl, Pražskou Dvojku.', 19);
INSERT INTO Bacon (id, value, card_id) VALUES (23, 'Jako autor je nejvíce znám příběhy chlapeckého klubu Rychlé šípy, knihami Hoši od Bobří řeky, Přístav volá, Chata v Jezerní kotlině, Záhada hlavolamu a jinými.', 19);
INSERT INTO Card (id, name, image) VALUES (149, 'František Halas', 'resources/img/authors/01halas.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (150, 'Narodil se 3. října 1901 v Brně. Zemřel 27. října 1949 v Praze.', 149);
INSERT INTO Bacon (id, value, card_id) VALUES (151, 'Po ukončení základní školy se vyučil knihkupcem.', 149);
INSERT INTO Bacon (id, value, card_id) VALUES (152, 'Otec ho přivedl mezi komunistickou mládež, která ho velmi ovlivnila, od r. 1921 pracoval pro komunistický tisk (Rovnost a Sršatec) a vydával avantgardní časopisy, na kterých spolupracoval s Bedřichem Václavkem (Pásmo a Fronta). Jeho tvorbu ovlivnil Jiří Mahen a pobyt ve Španělsku, kde byl v průběhu španělské občanské války. Za 2. světové války přispíval do ilegálně vydávaného Rudého práva. Byl členem revolučního výboru spisovatelů.', 149);
INSERT INTO Bacon (id, value, card_id) VALUES (153, 'Po roce 1945 se stal předsedou Syndikátu českých spisovatelů, pracoval na ministerstvu informací a choval se jako aktivní komunista. V letech 1945-1946 byl poslancem Prozatímního Národního shromáždění za KSČ. V parlamentu zasedal do parlamentních voleb v roce 1946. Později (především po svém návratu z návštěvy v Sovětském svazu) byl však novým režimem zklamán.', 149);
INSERT INTO Bacon (id, value, card_id) VALUES (154, 'Všechny básně sbírky se vyznačují syrovostí, snahou o maximální úspornost, sevřenost. Reakce na poválečný vývoj se objevují jen v náznaku, který umožňuje autorovi jeho obrazy zobecnit a vyslovit obavy o osud nejen českého národa, ale celé civilizace. Objevují se autobiografické motivy, úvahy o smyslu a možnostech poezie, závěrečná báseň A co básník vyslovuje básníkovo krédo.', 149);
INSERT INTO Bacon (id, value, card_id) VALUES (155, 'Roku 1997 mu byl in memoriam propůjčen Řád T. G. Masaryka II. třídy.', 149);
INSERT INTO Card (id, name, image) VALUES (156, 'Vladimír Holan', 'resources/img/authors/01holan.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (157, 'Narodil se 16. 9. 1905 v Praze. Zemřel 31. 3. 1980 v Praze.', 156);
INSERT INTO Bacon (id, value, card_id) VALUES (158, 'Vladimír Holan byl básník, autor reflexivní lyriky, epiky a lyrizovaných próz, překladatel.', 156);
INSERT INTO Bacon (id, value, card_id) VALUES (159, 'V roce 1926 složil maturitní zkoušku na gimnáziu v Praze a už tehdy vydal svou sbírku - Blouznivý vějíř.', 156);
INSERT INTO Bacon (id, value, card_id) VALUES (160, 'S přispěním básníka J. Hory dostal Vladimír Holan místo úředníka Penzijního ústavu v Praze. Zde pracoval v letech 1927 - 35, ale pro nevhodnost povolání a ze zdravotních důvodů odešel předčasně do penze. Poté se věnoval jen literární tvorbě.', 156);
INSERT INTO Bacon (id, value, card_id) VALUES (161, 'Vladimír Holan žil samotářsky, aby měl klid ke svým básnickým reflexím a meditacím. Styk udržoval jen s nejbližšími přáteli, k nimž patřili zvláště J. Hora, J. Čapek, F. Halas a někteří další.', 156);
INSERT INTO Bacon (id, value, card_id) VALUES (162, 'Tvorba Vladimíra Holana je pro svou filozofickou hloubku i volbu jazykových prostředků čtenářsky náročná, patří k vrcholným dílům české poezie.', 156);
INSERT INTO Bacon (id, value, card_id) VALUES (163, 'V 50. letech bylo postavení Vladimíra Holana jako básníka velmi svízelné, jeho poezie byla z hlediska vládnoucí ideologie nepřijatelná. Mohl existovat jen díky svým přátelům. Částečné uvolnění nastalo v 60. letech. Po smrti dcery Kateřiny v roce 1977 přestal Holan psát úplně.', 156);
INSERT INTO Card (id, name, image) VALUES (59, 'Bohumil Hrabal', 'resources/img/authors/01hrabal.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (60, 'Narodil se 28. března 1914 v Brně. Zemřel 3. února 1997 v Praze.', 59);
INSERT INTO Bacon (id, value, card_id) VALUES (61, 'Český prozaik, jeden z nejvýznamnějších a nejosobitějších spisovatelů druhé poloviny 20. století. Stal se nejpřekládanějším českým autorem 20. století.', 59);
INSERT INTO Bacon (id, value, card_id) VALUES (62, 'Spousta jeho děl byla zfilmována: Postřižniny, Slavnosti sněženek, Obsluhoval jsem anglického krála. Scénář k filmu Ostře sledované vlaky tvořil Jiří Menzel. Film byl odděněn Oscarem.', 59);
INSERT INTO Bacon (id, value, card_id) VALUES (63, 'Nejznámější díla: Pábitelé, Něžný barbar aj.', 59);
INSERT INTO Bacon (id, value, card_id) VALUES (64, 'Bylo po něm pojmenováno veřejné prostranství v Praze 8 na Palmovce – náměstí Bohumila Hrabala a také nedaleká Základní škola Bohumila Hrabala.', 59);
INSERT INTO Card (id, name, image) VALUES (49, 'Josef Čapek', 'resources/img/authors/01josefcapek.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (50, 'Narodil se 23. března 1887 v Hronově. Zemřel v dubnu 1945 /přesně mezi 13. a 15. dubnem/ v koncentračním táboře Bergen-Belsen.', 49);
INSERT INTO Bacon (id, value, card_id) VALUES (51, 'Konce války se již bohužel nedočkal.', 49);
INSERT INTO Bacon (id, value, card_id) VALUES (52, 'Český malíř, spisovatel, fotograf, grafik a knižní ilustrátor. Autor slova „robot“.', 49);
INSERT INTO Bacon (id, value, card_id) VALUES (53, 'Byl spoluzakladatel Skupiny výtvarných umělců, poté člen Spolku výtvarných umělců Mánes, s kterou se rozešel aby pak působil ve skupině Tvrdošíjných.', 49);
INSERT INTO Bacon (id, value, card_id) VALUES (54, 'Jako jevištní výtvarník spolupracoval s Národním divadlem v Praze, Státním divadlem v Brně a s Městským divadlem na Vinohradech.', 49);
INSERT INTO Bacon (id, value, card_id) VALUES (55, 'Začínal jako redaktor Národních listů, později (1921–1939) byl redaktorem a výtvarným kritikem v Lidových novinách. Mimo to působil jako redaktor v několika časopisech zabývajících se výtvarným uměním Umělecký měsíčník, Volné směry. Působil i jako karikaturista.', 49);
INSERT INTO Bacon (id, value, card_id) VALUES (56, 'Za protifašistickou činnost byl v září 1939 zatčen a do roku 1945 vězněn v nacistických koncentračních táborech. Tam také zemřel. Jeho symbolický hrob se nachází v Praze na Vyšehradském hřbitově.', 49);
INSERT INTO Bacon (id, value, card_id) VALUES (57, 'Napsal divadelní hru Adam stvořitel.', 49);
INSERT INTO Bacon (id, value, card_id) VALUES (58, 'Mezi jeho záliby patřila tvorba fejetonů.', 49);
INSERT INTO Card (id, name, image) VALUES (38, 'PhDr. Karel Čapek', 'resources/img/authors/01karelcapek.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (39, 'Narodil se 9. ledna 1890 v Malých Svatoňovicích. Zemřel 25. prosince 1938 v Praze.', 38);
INSERT INTO Bacon (id, value, card_id) VALUES (40, 'Český spisovatel, intelektuál, novinář, dramatik, překladatel a fotograf.', 38);
INSERT INTO Bacon (id, value, card_id) VALUES (41, 'Světoznámá osobnost. Se svým bratrem vymysleli mezinárodní slovo robot.', 38);
INSERT INTO Bacon (id, value, card_id) VALUES (42, 'Navazoval na Nerudu. Přítel T. G. M.', 38);
INSERT INTO Bacon (id, value, card_id) VALUES (43, 'Nenáviděl fašismus, to se mu stalo v jistém slova smyslu osudným.', 38);
INSERT INTO Bacon (id, value, card_id) VALUES (44, 'Napsal spoustu próz, fejetonů, článků do novin.', 38);
INSERT INTO Bacon (id, value, card_id) VALUES (45, 'Nejznámější díla: Krakonošova zahrada, Trapné povídky, Krakatit, Matka, Továrna na absulutno, Povídky z jedné kapsy, Povídky z druhé kapsy, Zahradníkům rok, Kniha apokryfů, Hordubal, Povětroň, R. u. R. Válka s mloky, Ze života hmyzu, Věc Akropolus, Bílá nemoc.', 38);
INSERT INTO Bacon (id, value, card_id) VALUES (46, 'Rád cestoval. Vydával i cesopisy (např. Anglické listy).', 38);
INSERT INTO Bacon (id, value, card_id) VALUES (47, 'Byl autorem i politických děl či překladů.', 38);
INSERT INTO Bacon (id, value, card_id) VALUES (48, 'Tvorba pro děti: Devatero pohádek, Dášenka čili život štěněte.', 38);
INSERT INTO Card (id, name, image) VALUES (118, 'Karel Hynek Mácha', 'resources/img/authors/01macha.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (119, 'Narodil se 16. listopadu 1810 v Praze. Umírá 6. listopadu 1836 Litoměřice po požáru domu, kde pomáhal při hašení ohně.', 118);
INSERT INTO Bacon (id, value, card_id) VALUES (120, 'Český básník a prozaik, představitel českého romantismu a zakladatel moderní české poezie.', 118);
INSERT INTO Bacon (id, value, card_id) VALUES (121, 'Proslavil se jak svým životem, tak dílem, jemuž dominuje lyrickoepická skladba Máj (1836), jedna z nejvydávanějších českých knih.', 118);
INSERT INTO Bacon (id, value, card_id) VALUES (122, 'Rád cestoval. Chodil na túry. Miloval krajinu kolem hradu Křivoklát, zříceniny hradu Trosky a všechna další temná zákoutí okolí České ráje a Českého středohoří.', 118);
INSERT INTO Bacon (id, value, card_id) VALUES (123, 'Byl nejen básníkem, ale i malířem.', 118);
INSERT INTO Bacon (id, value, card_id) VALUES (124, 'Máchovou první doloženou láskou byla Marinka Stichová (1810–1853), s níž se seznámil 15. srpna 1832 v Benešově. Máchovi imponoval nejen její vzhled, ale i to, že znala a četla české knihy. Ozvěny vztahu k Marince se promítly i do Máchovy tvorby. Samotný vztah neměl dlouhého trvání. Každopádně ještě v roce 1833 se v Pouti krkonošské Mácha vyznává ze ztracené lásky.', 118);
INSERT INTO Bacon (id, value, card_id) VALUES (125, 'Na konci roku 1832 zahořel láskou k mladinké herečce Rošrové, ale výsledkem vzplanutí byla jen báseň Panně Rošrové.', 118);
INSERT INTO Bacon (id, value, card_id) VALUES (126, 'Skutečnou Máchovou partnerkou, snoubenkou a matkou jeho syna Ludvíka byla Eleonora Šomková. Mácha se s ní seznámil někdy v zimě 1833/1834 po divadelní zkoušce. Konkrétně v Celetné ulici v domě U Červeného orla (dnes čp. 593/21), kde v té době byla kavárna U Suchých. K seznámení s Lori došlo za přítomnosti Josefa Kajetána Tyla a jeho pozdější ženy Leni Forchheimové. Charakter jejich vztahu, jejž ukončila po téměř třech letech Máchova smrt, nastiňuje Máchův intimní deník z roku 1835 a jeho dopisy. Podstatnou roli hrála v tomto vztahu Máchova povaha, jeho přecitlivělost a žárlivost, své učinili i rodiče mladých partnerů, ale třeba i Lořina neochota naučit se česky.', 118);
INSERT INTO Bacon (id, value, card_id) VALUES (127, '1. října 1836 porodila Lori Máchovi syna. Naposledy se s Máchou viděla 16. října 1836. Jejich svatba se měla konat 8. listopadu 1836 v kostele svatého Štěpána v Praze.', 118);
INSERT INTO Bacon (id, value, card_id) VALUES (128, 'Syn Ludvík zemřel 5. července 1837 na psotník.', 118);
INSERT INTO Card (id, name, image) VALUES (164, 'Oldřich Mikulášek', 'resources/img/authors/01mikulasek.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (165, 'Narodil se 26. 5. 1910 v Přerově. Zemřel 13. 7. 1985 v Brně', 164);
INSERT INTO Bacon (id, value, card_id) VALUES (166, 'Oldřich Mikulášek byl básník protiromantické, výsostně lyrické poezie s dramatickým napětím.', 164);
INSERT INTO Bacon (id, value, card_id) VALUES (167, 'Pracoval jako fakturista, správce cihelny na železnici, sběrač inzerátů, krátce ve Zlíně jako litograf a leptač. Teprve od roku 1937 působil jako redaktor, např. v Lidových novinách, Svobodných novinách, v Československém rozhlasu, Rovnosti či v Hostu do domu.', 164);
INSERT INTO Bacon (id, value, card_id) VALUES (168, 'V roce 1967 utrpěl těžký úraz, který ho dlouhodobě upoutal na lůžko a po vyléčení mu stejně znesnadnil pohyb.', 164);
INSERT INTO Bacon (id, value, card_id) VALUES (169, 'Verše Oldřicha Mikuláška jsou velice osobité, jazykově bohaté, obrazově silné a přesvědčivé. V jeho poezii se objevuje stoupající sklon k dramatičnosti, k existenciálnímu prožitku lidství. Tento uzlový bod lidství nikdy z jeho poezie nezmizel, i když po roce 1948, nebyl v souladu s tím, co se od básnictví žádalo tj. oslava horoucího, nového, radostného a optimistického života.', 164);
INSERT INTO Bacon (id, value, card_id) VALUES (170, 'Ve sbírkách z první poloviny 40. let v podstatě navazuje na halasovskou linii moderní české lyriky. Jsou psány pravidelným rýmovaným veršem, motivy mají symbolickou platnost (tráva, déšť, krev aj.)', 164);
INSERT INTO Card (id, name, image) VALUES (113, 'Vladimír Neff', 'resources/img/authors/01neffvladimir.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (114, 'Narodil se 13. června 1909 v Praze, zemřel 2. července 1983 taktéž v Praze.', 113);
INSERT INTO Bacon (id, value, card_id) VALUES (115, 'Český spisovatel, překladatel a scenárista, otec spisovatele Ondřeje Neffa.', 113);
INSERT INTO Bacon (id, value, card_id) VALUES (116, 'Nejznámější díla: Zlá krev, Sňatky z rozumu, Císařské fialky, Veselá vdova, Královský vozataj.', 113);
INSERT INTO Bacon (id, value, card_id) VALUES (117, 'Některá jeho díla posloužila i jako scénář pro filmovou tvorbu.', 113);
INSERT INTO Card (id, name, image) VALUES (8, 'Božena Němcová', 'resources/img/authors/01nemcova.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (9, 'Narozena 4. února 1820 ve Vídni. Zemřela 21. ledna 1862 v Praze.', 8);
INSERT INTO Bacon (id, value, card_id) VALUES (10, 'Rozená Barbora Novotná, později Barbora Panklová, česká spisovatelka. Je považována za zakladatelku novodobé české prózy.', 8);
INSERT INTO Bacon (id, value, card_id) VALUES (11, 'Dodnes se vedou spory o jejím původu. Historici se přou o tom, kdo byli její biologičtí rodiče.', 8);
INSERT INTO Bacon (id, value, card_id) VALUES (12, 'Božena Němcová prožila celý život v ponižující chudobě a často i v hladu. Její povzdech: Těžko povznésti ducha, když starost o chléb vezdejší jej tíží. Korespondence ukazuje, že byla nucena opakovaně žádat o pomoc v českých vlasteneckých kruzích. Neúčinnost této pomoci kontrastuje s velkolepým pohřbem, který jí vlastenci uspořádali a s posmrtnou slávou, které se jí dostalo. Pohřbena je na Vyšehradě. Její hrob byl původně vedle hrobu Václava Hanky, ale po smrti jeho ženy Barbory byly ostatky Boženy Němcové přeneseny na současné místo. Její muž byl pohřben v Táboře, kde zemřel u svého syna Karla.', 8);
INSERT INTO Bacon (id, value, card_id) VALUES (13, 'Jednou ve svých listech přátelům píše: ,,Nemohu nikam vyjít z domu, neboť nemám střevíce.', 8);
INSERT INTO Bacon (id, value, card_id) VALUES (14, 'Nejznámější próza: Babička, Divá Bára, V zámku a podzámčí, Pohádky, Národní báchorky a pověsti, Cesta z pouti, Pan učitel, Chudí lidé, Chyže pod horami, Karla, Čertův švagr.', 8);
INSERT INTO Bacon (id, value, card_id) VALUES (15, 'Sběratelka ústní lidové slovesnosti.', 8);
INSERT INTO Bacon (id, value, card_id) VALUES (16, 'Měla hodně přátel v oblasti kultury a českého vlastenectví. Např. K. H. Borovského.', 8);
INSERT INTO Bacon (id, value, card_id) VALUES (17, 'Hodně jejích děl bylo zfilmováno, at už jako pohádky pro či předlohy ke scénářům k filmům.', 8);
INSERT INTO Bacon (id, value, card_id) VALUES (18, 'Celkem čtyři děti, 3 synové a 1 dcera. Syn Hynek později umírá, na popud svého smutku píše prózu Babička (vzpomínky na dětství).', 8);
INSERT INTO Card (id, name, image) VALUES (107, 'Josef Nesvadba', 'resources/img/authors/01nesvatba.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (108, 'Narodil se 19. června 1926 v Praze, zemřel 26. dubna 2005 taktéž v Praze.', 107);
INSERT INTO Bacon (id, value, card_id) VALUES (109, 'Český lékař a spisovatel. V období socialismu byl spolu s Ludvíkem Součkem nejvýznamnějším českým představitelem žánru vědeckofantastické literatury (science fiction). Jeho tvorba pokračovala dále i po sametové revoluci v roce 1989.', 107);
INSERT INTO Bacon (id, value, card_id) VALUES (110, 'Narodil se do učitelské rodiny, otec byl profesorem na obchodní škole a také matka byla učitelkou. Studia začal na anglickém gymnáziu, z něhož v roce 1941 přešel na gymnázium české. Jako mnoha jiným, i Nesvadbovi zkomplikovala studia na gymnáziu nacistická okupace, maturovat mohl až po válce v roce 1945.', 107);
INSERT INTO Bacon (id, value, card_id) VALUES (111, 'Od 19 let využil znalost angličtiny, začal s prvními překlady. V letech 1945–1950 absolvoval lékařskou fakultu v Praze, obor psychiatrie – vedle své literární práce byl i významných psychiatrem a patřil k průkopníkům skupinové psychoterapie v Česku. Právě během studia poznal i Ludvíka Součka a oba tvůrci sci-fi se spřátelili. V letech 1949–1950 byl scenáristou v Československém státním filmu. V roce 1950 nastoupil do interny nemocnice v Teplicích, kde pracoval do roku 1952. Pak působil jako vojenský lékař u letectva a v Armádním uměleckém souboru. Od roku 1956 až do odchodu do důchodu v roce 1992 pracoval jako psychoterapeut na psychiatrickém oddělení Fakultní polikliniky v Praze. Byl evidován také jako agent STB pod krycím jménem Autor.[1] Kromě překladů z angličtiny psal převážně divadelní hry a povídky - několik jeho povídek bylo i zfilmováno, například Blbec z Xeenemünde (1960) a Tarzanova smrt.', 107);
INSERT INTO Bacon (id, value, card_id) VALUES (112, 'Otec Barbary Nesvadbové (taktéž píše, šéfredaktorka společenského měsíčníku Bazar.', 107);
INSERT INTO Card (id, name, image) VALUES (171, 'Jaroslav Seifert', 'resources/img/authors/01seifert.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (172, 'Narodil se 23. 9. 1901 v Praze. Zemřel 10. 1. 1986 v Praze.', 171);
INSERT INTO Bacon (id, value, card_id) VALUES (173, 'V roce 1984 obdržel Nobelovu cenu za literaturu, kterou však za něj přebírala jeho dcera, a to kvůli jeho špatnému zdravotnímu stavu. Ačkoli to byla velmi významná událost, ve sdělovacích prostředcích ovládaných tehdejším režimem o tom padla jenom suchá zmínka.', 171);
INSERT INTO Bacon (id, value, card_id) VALUES (174, 'Studoval na žižkovském gymnáziu, potom přešel do vinohradského, avšak ani tady nedospěl k maturitě. Ze školy odešel a rozhodl se v roce 1921 pro práci žurnalisty. Působil v Rudém právu a Rovnosti.', 171);
INSERT INTO Bacon (id, value, card_id) VALUES (175, 'Jaroslav Seifert byl spoluzakladatelem Devětsilu a redaktorem Reflektoru. Ve dvacátých letech přispíval do Proletkultu, Tvorby a ReDu, od počátku třicátých let do Avantina, Kvartu, Lisrů pro umění a kritiku.', 171);
INSERT INTO Bacon (id, value, card_id) VALUES (176, 'V roce 1929 byl mezi sedmi spisovateli, kteří podepsali manifest proti gottwaldovskému vedení KSČ. Následně byl vyloučen ze strany.', 171);
INSERT INTO Bacon (id, value, card_id) VALUES (177, 'Po roce 1945 vedl literární časopis Kytice a redigoval básnickou edici Klín (nakladatelství Práce). Básně tiskl (od roku 1920) především v Právu lidu, Kmeni a Červnu.', 171);
INSERT INTO Bacon (id, value, card_id) VALUES (178, 'Počátkem roku 1986 Jaroslav Seifert umírá. Státní pohřeb v Rudolfinu hrozil přerůst v protikomunistickou manifestaci, a proto ministerstvo vnitra z příprav pohřbu vyloučilo rodinu. Církevní rozloučení konané v břevnovském kostele sv. Markéty bylo pod dohledem státní bezpečnosti. Místem posledního spočinutí Jaroslava Seiferta jsou Kralupy nad Vltavou, odkud pocházeli jeho prarodiče z matčiny strany.', 171);
INSERT INTO Bacon (id, value, card_id) VALUES (179, 'V prosinci 1976 patřil mezi první signatáře Charty 77. Za své aktivní vystupování byl v období tzv. normalizace nucen odejít do ústraní. V tomto období byl stále více orientován na český disent, jeho díla pravidelně vycházela v samizdatu.', 171);
INSERT INTO Card (id, name, image) VALUES (129, 'Václav Kaplický', 'resources/img/authors/01vaclavkaplicky.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (130, 'Narodil se 28. srpna 1895 v Červeném Dvoře, okres Tábor. Zemřel 4. října 1982 v Praze.', 129);
INSERT INTO Bacon (id, value, card_id) VALUES (131, 'Český prozaik, publicista, autor beletristických kronik z českých dějin a legionářské prózy. Především je znám jako autor historické beletrie.', 129);
INSERT INTO Bacon (id, value, card_id) VALUES (132, 'Po návratu do Československa (1919), se stal zaměstnancem sekretariátu socialistického Svazu československých legionářů, krátce na to byl (1919-1922) úředníkem ministerstva národní obrany.', 129);
INSERT INTO Bacon (id, value, card_id) VALUES (133, 'V letech 1922–1950 pracoval v několika nakladatelstvích (např. Čin, Pokrok, Družstevní práce, Melantrich), nejprve jako úředník a později jako odpovědný redaktor, kupř: v roce 1926 pracoval v redakci Cestami odboje - Jak žily a kudy táhly československé legie. Od roku 1950 se věnoval pouze literatuře. Snažil se o prohloubení lidové četby kritickým pohledem zejména na sociální problémy, ale později se věnoval výhradně historické próze a těžištěm jeho tvorby se stala doba husitská, doba čarodějnických procesů a bezpráví, a také různá povstání, vzpoury a lidový odpor.', 129);
INSERT INTO Bacon (id, value, card_id) VALUES (134, 'Václav Kaplický je pochován na čestném místě - vyšehradském hřbitově. Busta na jeho hrobě je dílem sochaře Břetislava Bendy, autora. řady portrétů známých kulturních osobností.', 129);
INSERT INTO Bacon (id, value, card_id) VALUES (135, 'Snad nejznámější kniha má název Kladivo na čarodějnice. Byla i zfilmována.', 129);
INSERT INTO Card (id, name, image) VALUES (180, 'Vladislav Vančura', 'resources/img/authors/01vancura.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (181, 'Narodil se 23. června 1891 v Háji ve Slezsku. Zemřel 1. června 1942, popraven za heydrichiády v Praze.', 180);
INSERT INTO Bacon (id, value, card_id) VALUES (182, 'Jeho otec se dlouhou dobu nemohl uplatnit, a tak se často stěhovali. Dětství prožil na venkově, kde získal lásku k přírodě a koním. Byl velmi hrdý a nepoddajný, takže jeho studijní výsledky nebyly příliš dobré.', 180);
INSERT INTO Bacon (id, value, card_id) VALUES (183, 'Byl velmi hrdý a nepoddajný, takže jeho studijní výsledky nebyly příliš dobré. Na vysoké škole studoval nejdříve práva, ale po těžké nemoci matky přestoupil na medicínu. Pracoval jako lékař ve Zbraslavi. Jeho bratrancem byl Jiří Mahen.', 180);
INSERT INTO Bacon (id, value, card_id) VALUES (184, 'Byl členem a prvním předsedou Devětsilu (1920), přívrženec poetismu, ve třicátých letech byl členem Levé fronty. Od roku 1919 publikoval v mnoha časopisech – Červen, Kmen, Host a Panorama. Od roku 1921 byl členem KSČ, ale jeho dílo nelze označit, na rozdíl od tvorby jeho mnohých současníků, za komunistickou agitku. Z KSČ vystoupil v roce 1929 spolu s řadou dalších umělců v reakci na nástup Gottwaldova vedení.', 180);
INSERT INTO Bacon (id, value, card_id) VALUES (185, 'V roce 1924 působil v období března až května jako divadelní kritik v deníku Československá samostatnost, později přejmenovaného na Národní osvobození, kde převzal místo po Jindřichu Vodákovi, který odešel do divadelní rubriky Českého slova. Kritiky podepisoval šifrou -jv.- nebo -W-. Celkově zde připravil 21 divadelních kritik.', 180);
INSERT INTO Bacon (id, value, card_id) VALUES (186, 'Byl předsedou české filmové společnosti.', 180);
INSERT INTO Bacon (id, value, card_id) VALUES (187, 'V roce 1946 byl in memoriam jmenován národním umělcem.', 180);
INSERT INTO Bacon (id, value, card_id) VALUES (188, 'Za okupace vedl spisovatelskou sekci Výboru inteligence – ilegální odbojové organizace při komunistickém Ústředním národním revolučním výboru. V květnu roku 1942 byl zatčen a 1. června na Kobyliské střelnici popraven.', 180);
INSERT INTO Bacon (id, value, card_id) VALUES (189, 'Archaická slova a knižní výrazy spojuje s lidovou mluvou. Větná stavba je zastaralá, užívá složitá souvětí, zdůrazňuje jimi důležitost daného okamžiku. Tento jazyk dává dílům monumentální ráz. Děj je potlačován, důležité místo má vypravěč, který vyjadřuje svůj názor k ději, přerušuje vyprávění a oslovuje čtenáře, hodnotí chování postav. Části příběhů připomínají filmové scénáře. Opěvuje život, zdůrazňuje prožitky, detailně vyjadřuje náladu, barevnost příběhu, city.', 180);
INSERT INTO Card (id, name, image) VALUES (95, 'Vlasta Javořická', 'resources/img/authors/01vlastajavoricka.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (96, 'Narozena 25. března 1890 ve Studené u Telče. Zemřela 1. srpna 1978 ve Velkém Újezdě u Moravských Budějovic.', 95);
INSERT INTO Bacon (id, value, card_id) VALUES (97, 'Česká spisovatelka, autorka velkého počtu románů a povídek z venkova, jejichž osou jsou především milostné příběhy.', 95);
INSERT INTO Bacon (id, value, card_id) VALUES (98, 'Pocházela z lékařské rodiny.', 95);
INSERT INTO Bacon (id, value, card_id) VALUES (99, 'Vlastním jménem Marie Barešová, provdaná Zezulková.', 95);
INSERT INTO Bacon (id, value, card_id) VALUES (100, 'Napsala asi 150 knih - románů o ženách a o životě. Její díla vycházejí i posmrtně z pozůstalosti.', 95);
INSERT INTO Bacon (id, value, card_id) VALUES (101, 'Její romány jsou stále oblíbené a čtou se.', 95);
INSERT INTO Card (id, name, image) VALUES (102, 'Vlasta Pittnerová', 'resources/img/authors/');
INSERT INTO Bacon (id, value, card_id) VALUES (103, 'Narodila se 1. ledna 1858 v Polné. Zemřela 2. března 1926 v Praze.', 102);
INSERT INTO Bacon (id, value, card_id) VALUES (104, 'Spisovatelka, autorka naivně realistických, často mravoučných próz ze života obyvatel Českomoravské vysočiny a zejména Žďárských hor.', 102);
INSERT INTO Bacon (id, value, card_id) VALUES (105, 'Počet románů: asi 150.', 102);
INSERT INTO Bacon (id, value, card_id) VALUES (106, 'Narodila se v rodině knihkupce a starosty města Antonína Pittnera. Prostředí, ve kterém vyrůstala, přálo kulturním a vlasteneckým zájmům. Její otec byl přítelem Karla Havlíčka Borovského a Boženy Němcové. Matka Anna byla dokonce kmotrou dcery Němcové – Theodory (1841).', 102);
INSERT INTO Card (id, name, image) VALUES (142, 'Jiří Wolker', 'resources/img/authors/01wolker.jpg');
INSERT INTO Bacon (id, value, card_id) VALUES (143, 'Narodil se 29. března 1900 v Prostějově. Zemřel 3. ledna 1924 v Prostějově.', 142);
INSERT INTO Bacon (id, value, card_id) VALUES (144, 'V letech 1916–1917 se pod vedením Antonína Svojsíka účastnil skautských táborů ve Vorlovských lesích nedaleko hradu Lipnice. Byl nadšený Sokol a skaut.', 142);
INSERT INTO Bacon (id, value, card_id) VALUES (145, 'Studoval práva v Praze, přičemž zároveň navštěvoval literární přednášky. Byl politicky činný v KSČ.', 142);
INSERT INTO Bacon (id, value, card_id) VALUES (146, 'Trpěl vážnou plicní chorobou – tuberkulózou, která se u něj projevovala již v dětství častými záněty mandlí a katary průdušek, nakonec jí podlehl.', 142);
INSERT INTO Bacon (id, value, card_id) VALUES (147, 'S jeho jménem je často spojován nový literární směr – proletářské umění.', 142);
INSERT INTO Bacon (id, value, card_id) VALUES (148, 'Napsal si u svůj vlastní epitaf, jeho pozdní dílo je samozřejmě také ovlivněno jeho těžkou chorobou, proto zde často nacházíme hřbitovy či nemocnice. „Zde leží Jiří Wolker, básník, jenž miloval svět a pro spravedlnost jeho šel se bít. Dřív než moh srdce k boji vytasit, zemřel – mlád dvacet čtyři let.“ — Wolkerův epitaf', 142);
